function LoadChildCategoryList(c,d,f){if(c.indexOf("__AJAX=True")<0)c+="&__AJAX=True";$.get(c,null,function(a){var e=$("#"+d);e.html("");if(f)for(var b in f)e.append('<option value="'+b+'">'+f[b]+"</option>");a=eval(a).Data;for(b=0;b<a.length;b++){var g=a[b];e.append('<option value="'+g.CategoryId+'">'+g.Name+"("+g.UrlName+")</option>")}})}
function DeleteArticles(c,d){if(window.confirm(d)){c=$("#"+c).attr("action");var f=[];$("input[name='ArticleIds']").each(function(){var a=$(this);a.attr("checked")&&f.push(a.val())});d={ArticleIds:f.join(",")};PostAction(c,d,"ActionMessageBox","SubmitDeleteChecked",function(a){var e=parseInt(a.Result);if(a=eval(a.Data))for(e=0;e<a.length;e++){var b=$("#articleItem_"+a[e]);b.fadeOut(400);setTimeout(function(){b.remove()},500)}return true})}return false}
function BindDeleteAttachFunction(c){$("#ArticleAttachList .delete_attach_btn").each(function(){var d=$(this),f=d.attr("id");d.unbind("click");d.bind("click",function(){QueryLinkAction(f,c,function(a){var e=parseInt(a.Result),b=$("#AttachListItem_"+a.Data);b.fadeOut(400);setTimeout(function(){b.remove()},500);return true});return false})})};
