function DeleteComments(c,d){if(window.confirm(d)){c=$("#"+c).attr("action");var e=[];$("input[name='CommentIds']").each(function(){var a=$(this);a.attr("checked")&&e.push(a.val())});d={CommentIds:e.join(",")};PostAction(c,d,"ActionMessageBox","SubmitDeleteChecked",function(a){var b=parseInt(a.Result);a=eval(a.Data);for(b=0;b<a.length;b++){var f=$("#commentItem_"+a[b]);f.fadeOut(400);setTimeout(function(){f.remove()},500)}return true})}return false};
