function DeleteTopics(c,b){if(window.confirm(b)){c=$("#"+c).attr("action");var f=[];$("input[name='TopicIds']").each(function(){var a=$(this);a.attr("checked")&&f.push(a.val())});b={TopicIds:f.join(",")};PostAction(c,b,"ActionMessageBox","SubmitDeleteChecked",function(a){var d=parseInt(a.Result);if(a=eval(a.Data))for(d=0;d<a.length;d++){var e=$("#topicItem_"+a[d]);e.fadeOut(400);setTimeout(function(){e.remove()},500)}return true})}return false}
function BindDeleteAttachFunction(c){$("#TopicAttachList .delete_attach_btn").each(function(){var b=$(this),f=b.attr("id");b.unbind("click");b.bind("click",function(){QueryLinkAction(f,c,function(a){var d=parseInt(a.Result),e=$("#AttachListItem_"+a.Data);e.fadeOut(400);setTimeout(function(){e.remove()},500);return true});return false})})};
